    prevButton.addEventListener('click', () => {
        trendingShows.scrollBy({
            left: -scrollAmount,
            behavior: 'smooth'
        });
    });

    nextButton.addEventListener('click', () => {
        trendingShows.scrollBy({
            left: scrollAmount,
            behavior: 'smooth'
        });
    });

